import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import Notification from "./components/UI/Notification";
import { useEffect } from "react";

import { cartToggleActions } from "./store/cartToggleSlice";
import { useSelector, useDispatch } from "react-redux";
import { notInitialized } from "react-redux/es/utils/useSyncExternalStore";

let isInitialized = true;

function App() {
  const dispatch = useDispatch();
  const isShow = useSelector((state) => state.cartToggle.isShow);

  const cart = useSelector((state) => state.cart);
  const notification = useSelector((state) => state.cartToggle.notification);

  useEffect(() => {
    const sendCartData = async () => {
      dispatch(
        cartToggleActions.showNotification({
          status: "pending",
          title: "Sending...",
          message: "Sending cart data!",
        })
      );

      const response = await fetch(
        "https://redux-http-9231c-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );

      if (!response.ok) {
        throw new Error("sending cart data failed");
      }

      dispatch(
        cartToggleActions.showNotification({
          status: "success",
          title: "Success!.",
          message: "Sent cart data sucessfully!",
        })
      );
      // const responseData = await response.json();
    };

    if(isInitialized){
      isInitialized = false
    }

    sendCartData().catch((error) => {
      dispatch(
        cartToggleActions.showNotification({
          status: "error",
          title: "Error!",
          message: "Sending cart data failed!",
        })
      );
    });
  }, [cart, dispatch]);

  return (
    <>
      {notification && (
        <Notification
          status={notification.status}
          title={notification.title}
          message={notification.message}
        ></Notification>
      )}
      <Layout>
        {isShow && <Cart />}
        <Products />
      </Layout>
    </>
  );
}

export default App;
