import React, { Component } from "react";

export class ErrorBoundary extends Component {
  constructor() {
    super();
    this.state = {
      hasError: false,
    };
  }

  componentDidCatch(error) {
    this.setState
  }

  render() {
    return this.props.children;
  }
}

export default ErrorBoundary;
