import { createSlice } from "@reduxjs/toolkit";

const initialCartState = { items: [], totalQuantity: 0, toatalAmount: 0 };

const cartSlice = createSlice({
  name: "cart-items",
  initialState: initialCartState,
  reducers: {
    addCart(state, action) {
      const newItem = action.payload;
      const ItemExist = state.items.find((val) => val.id === newItem.id);
      if (!ItemExist) {
        state.items.push({
          itemId: newItem.id,
          itemPrice: newItem.price,
          itemQuantity: 1,
          itemTitle:newItem.title
        });
      }
      else{
    ItemExist.itemQuantity = ItemExist.itemQuantity 
