import React from "react";
import classes from "./MealsItemForm.module.css";
import Input from "../../UI/Input";




const MealItemForm = (props) => {
  return (
    <form className={classes.form}>
      <Input
        label="Amount"
        input={{
          id: "amount",
          type: "number",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: "1",
        }}
      ></Input>
      <button>+ ADD</button>
    </form>
  );
};

export default MealItemForm;
