import React, { useState } from "react";

import "./App.css";

function App() {
  const [showParagraph, setshowParagraph] = useState(false);

  const toggleHandler = () => {
    setshowParagraph(prevShowParagraph);
  };

  return (
    <div className="app">
      <h1>Hi there!</h1>
      {showParagraph && <p>This is new!</p>}
      <button onClick={toggleHandler}>Toggle</button>
    </div>
  );
}

export default App;
